  private async parseUserCsvRow(
    row: any,
  ): Promise<Partial<UserDocument> | null> {
    // Parse row data to UserDocument format
    if (!row.user_id) return null;
    if (!row.name) return null;
    if (!row.image) return null;
    const skills = convertTagsToArray(row.skills);
    if (skills.length === 0) return null;
    const interestedIn = convertTagsToArray(row.interests);
    if (interestedIn.length === 0) return null;
    const user = new User({
      email: '',
      userType: [],
      password: '1',
    });

    await user.hashPassword();
    const links = JSON.parse(
      row.links
        .replace(/'/g, '"') // Thay ' bằng "
        .replace(/None/g, 'null') || '{}',
    );
    const parsedUser: Partial<UserDocument> = {
      email: row.user_id + '@gmail.com',
      password: user._props.password,
      fullname: row.name,
      username: row.user_id,
      avatar: row.image,
      isActive: true,
      isSetPersionalSetting: true,
      settingRecommend: {
        specialty: this.getRandomU('spe'),
        skills,
        interestedIn,
        occupation: this.getRandomU('ocu'),
        currentLevel: this.getRandomU('lv'),
      },
      achievements: row.achievements,
      bio: row.bio,
      address: row.location,
      githubLink: links.github || undefined,
      linkedinLink: links.linkedin || undefined,
      userType: [UserType.SEEKER],
    };
    return parsedUser;
  }

